sp_widget.script:
(function() {
	var gr = new GlideRecord('x_snc_accessprov_0_environment');
	var env_name = gr.getElement("name");
	data.env = env_name.getChoices(); // Read the Choices present in the Environment:Name field
	data.snTableName = 'x_snc_accessprov_0_automationlist'; // table name for snRecordPicker to show automation list
})();

if (input) {
	if (input.action == 'getData') {
		// Get data once UID is entered by user is valid
		gr = new GlideRecord('sys_user');
		gr.addEncodedQuery('user_name=' + input.uid);
		gr.query();
		while (gr.next()) {
			data.user_info = {
				fname: gr.first_name.toString(),
				lname: gr.last_name.toString(),
				email: gr.email.toString(),
				uid: input.uid,
			};
		}
	}

	if (input.action == 'createUser') {
		// validate if any of the required field is undefined
		if(input.uid == undefined || input.fname == undefined || input.lname == undefined || input.email == undefined || input.automation == undefined || input.environment == undefined)
			gs.addErrorMessage("Please fill all the neccessary fields and submit the form.");
		else {
			var gr = new GlideRecord('x_snc_accessprov_0_aa_request_access');
			gr.addEncodedQuery('x_m_uid=' + input.uid + "^automation_name=" + input.automation + "^environment=" + input.environment + "^status=pending^ORstatus=granted");
			gr.query();
			if (!gr.next()) {
				gr.initialize(); //create an empty record before inserting.
				gr.setValue('first_name', input.fname);
				gr.setValue('last_name', input.lname);
				gr.setValue('email_id', input.email);
				gr.setValue('automation_name', input.automation);
				gr.setValue('environment', input.environment);
				gr.setValue('x_m_uid', input.uid);
				var sysId = gr.insert();

				var user = {
					sysId: sysId,
					fname: input.fname,
					lname: input.lname,
					name: input.fname + " " + input.lname,
					email: input.email,
					uid: input.uid,
					automationName: input.automation,
					environment: input.environment
				};
				// Script Includes Class
				var apcf = new APCommonFunctions();
				var output = apcf.createOrRejectUser("create", user);
				gs.addInfoMessage("Request is made to AutomationAnywhere.");

				//return result to client
				if (output.isCreated)
					gs.addInfoMessage('User has been created. Notification will send to user"s mail');
				else
					gs.addErrorMessage('Unable to create user. Reason: ' + output.msg);


			} else {
				gs.addErrorMessage("Request is already made.");
			}
		}


	}


}
