sp_widget.client_script:
api.controller = function() {
    /* widget controller */
    var c = this;
};

sp_widget.css:
table td, table th{
  text-align: center;
}

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    data.adminAccessInfo = [];
    var gr = new GlideRecord('x_snc_accessprov_0_aa_request_access');
    gr.addQuery('x_m_uid', gs.getUserName());
    gr.query();
	var i = 1;
    while (gr.next()) {
        data.adminAccessInfo.push({
					no:i++,
            automationName: gr.getValue('automation_name').toString(),
            environment: gr.getValue('environment').toString(),
            status: "Pending"
        });
    }


    data.othersAccessInfo = [];
	gr = new GlideRecord('x_snc_accessprov_0_aa_request_access');  
		gr.addEncodedQuery('x_m_uidNOT LIKE' + gs.getUserName());
    gr.query();
		i = 1;
    while (gr.next()) {
			//gs.addInfoMessage(gr.getValue('automation_name'))

			data.othersAccessInfo.push({

					no:i++,
						name: gr.getValue('first_name').toString()+" " + gr.getValue('last_name').toString(),
						mailId: gr.getValue('email_id').toString(),
						uid: gr.getValue('x_m_uid').toString(),
            automationName: gr.getValue('automation_name').toString(),
            environment: gr.getValue('environment').toString(),
            status: "Pending"
        });

    }
})();
