sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	//data.table = options.table || $sp.getParameter("t") || $sp.getParameter("table") || 'x_snc_accessprov_0_aa_request_access';
	//data.sys_id = options.sys_id || $sp.getParameter("sys_id") || '3496c7e49f6731107f44c737f024ab45';
	gs.info("Reading the data");

	data.table = 'x_snc_accessprov_0_aa_request_access';
	data.sys_id = '5e2f88dd9f2331107f44c737f024ab6c';


	if(!data.table || !data.sys_id)
		return;

	var cm = new CommonMethods();
	//var rec = cm.getRecord(data.table, data.sys_id);	
	var rec = cm.getRecord(data.table, data.sys_id);
	gs.info(JSON.stringify(rec));
	if(!rec.isValid)
		return
	else{
		gs.info("Valid Record");

		var gdt = new GlideDateTime(rec.sys_created_on.toString());
		gdt.add(19800000);
		gs.info(gdt);
	}


})();
