sp_widget.client_script:
api.controller=function(spUtil,spModal) {
    /* widget controller */
    var c = this;

      // delete object property if exists
      function deleteProp(obj, key) {
          if(obj.hasOwnProperty(key)) { 
              delete obj[key];
          }
          return obj;
      }

      // Reset data properties
      function resetData() {
          deleteProp("action");
          deleteProp("userId");
          deleteProp("sysId");
      }

      function handleSuccess(res) {
          resetData();
          c.server.refresh();
      }

      function handleError(error) {
          // Handle or log the error as needed
          console.error('An error occurred:', error);
          resetData();
      }
      c.delete = function(userId, sysId){
          console.log(sysId);
          var warn = '<i class="fa fa-warning" aria-hidden="true"></i>';
          spModal.confirm("Are you sure to <b>remove</b> this user?").then(function(confirmed) {
              spUtil.addTrivialMessage("Removing user...");
              c.data.userId = userId;
          c.data.sysId = sysId;
          c.data.action = "removeUser";
          c.server.update().then(handleSuccess).catch(handleError);
          })

      }


  };

sp_widget.css:
th, td{
  text-align: center;
  vertical-align: middle !important;
}


.no-request{
  display: flex;
  justify-content: center;
  align-items: center;
  height: 300px;
  text-align: center;
  color: black;
  text-transform: uppercase;
}

sp_widget.script:
(function () {
	var apcf = new APCommonFunctions();
	data.userInfo = apcf.getAllApprovedUsers();

	if (input) {
		if (input.action == "removeUser") {
			try {
				var userId = input.userId;
				var sysId = input.sysId;
				if(apcf.removeUsr(userId, sysId))
					gs.addErrorMessage('An error occurred while removing user.');
				else
					gs.addInfoMessage("User has been removed");
			} catch (e) {
				gs.error('Error deleting user:', e);
				gs.addErrorMessage('An error occurred while removing user.'+e);
			}
		}
	}
})();
