sp_widget.client_script:
api.controller = function() {
    /* widget controller */
    var c = this;
};

sp_widget.css:
table td, table th{
  text-align: center;
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.accessInfo = [];
	var i = 1;
	var gr = new GlideRecord('x_snc_accessprov_0_aa_request_access');
	gr.addQuery('x_m_uid', gs.getUserName());
	gr.query();


	while (gr.next()) {
		data.accessInfo.push({
			no:i++,
			automationName: gr.getValue('automation_name').toString(),
			environment: gr.getDisplayValue('environment').toString(),
			status: gr.getDisplayValue('status').toString()
		})
	}
})();
