sp_widget.client_script:
api.controller = function(spUtil){
	var c = this;

	function deleteProp(obj, key) {
		if(obj.hasOwnProperty(key)) { 
			delete obj[key];
		}
		return obj;
	}

	function handleUpdateResponse(res) {
		deleteProp(c.data, "action");
		deleteProp(c.data, "fname");
		deleteProp(c.data, "lname");
		deleteProp(c.data, "uid");
		deleteProp(c.data, "automation");
		deleteProp(c.data, "email");
		deleteProp(c.data, "environment");
	}

	function handleError(error) {
		console.error('An error occurred:', error);
		deleteProp(c.data, "action");
	}

	c.requestAccess = function(){
		c.data.fname = c.data.user_info.fname;
		c.data.lname = c.data.user_info.lname;
		c.data.email = c.data.user_info.email;
		c.data.uid = c.data.user_info.uid;
		c.data.automation = c.data.temp_auto_name.value;
		c.data.environment = c.data.temp_env;
		c.data.action = 'requestAccess';
		c.server.update().then(handleUpdateResponse).catch(handleError);
	}


	/*
function onChange(control, oldValue, newValue, isLoading) { 
  var elementID = gel('txt_automation_name');
    switch(newValue) { 
      case "1": elementID.style.backgroundColor = "red"; break; 
