sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    var logName = "LogRequests: ";
    var logGR = new GlideRecord('x_snc_accessprov_0_access_logs');
    gs.info(logName+"logging the request/update");
    logGR.initialize(); //create an empty record before inserting.
    logGR.setValue('first_name', current.first_name);
    logGR.setValue('last_name', current.last_name);
    logGR.setValue('email_id', current.email_id);
    logGR.setValue('automation_name', current.automation_name);
    logGR.setValue('environment', current.environment);
    logGR.setValue('x_m_uid', current.x_m_uid);
	logGR.setValue('status', current.status);
	if(current.id)
		logGR.setValue('id', current.id);
	if(current.roles)
		logGR.setValue('roles', current.roles);
    logGR.insert();
	gs.info(logName+"logged successfully");

})(current, previous);
