sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var log_name = "GetAccess-BR: ";
    current.status = "pending";
    try {

        // Getting the Token
        var r = new sn_ws.RESTMessageV2('x_snc_accessprov_0.AA Authentication', 'Default Post');
        r.setStringParameterNoEscape('api_key', 'ii5_V`~@t<3oE|0h1XIQqIUc{|yWotEoK3Oryz^n');
        r.setStringParameterNoEscape('username', 'X253816');
        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();


        if (httpStatus == 200) {
            // Get the Access Token
            var tkn = JSON.parse(responseBody).token;
            gs.info(log_name + "User Creation | Token received.");

            var fname = current.getValue("first_name");
            var lname = current.getValue("last_name");
            var mailId = current.getValue("email_id");
            var uid = current.getValue("x_m_uid");

            /*
			var fname = current.first_name;
			var lname = current.last_name;
            var mailId = current.email_id;
            var uid = current.x_m_uid;
			*/

            r = new sn_ws.RESTMessageV2('x_snc_accessprov_0.AA Authentication', 'Create User');
            r.setRequestHeader("x-authorization", tkn);
            r.setStringParameterNoEscape('firstName', fname);
            r.setStringParameterNoEscape('lastName', fname);
            r.setStringParameterNoEscape('mailId', mailId);
            r.setStringParameterNoEscape('pass', 'abc@123123');
            r.setStringParameterNoEscape('userName', uid);
            r.setStringParameterNoEscape('id', '224');

            //override authentication profile 
            //authentication type ='basic'/ 'oauth2'
            //r.setAuthenticationProfile(authentication type, profile name);

            //set a MID server name if one wants to run the message on MID
            //r.setMIDServer('MY_MID_SERVER');

            //if the message is configured to communicate through ECC queue, either
            //by setting a MID server or calling executeAsync, one needs to set skip_sensor
            //to true. Otherwise, one may get an intermittent error that the response body is null
            //r.setEccParameter('skip_sensor', true);


            gs.info(log_name + "User Creation | Creating User...");

            response = r.execute();
            responseBody = response.getBody();
            httpStatus = response.getStatusCode();
            gs.info(log_name + "User Creation | Result - HTTP status Code: " + httpStatus);
            if (httpStatus == 201) {
                gs.info(log_name + "User Creation | Success - Updating values to table");
                current.status = "granted";
                gs.info(log_name + "User Creation | Current Status: " + current.status);
                gs.info(log_name + "User Creation | ID: " + JSON.parse(responseBody).id);
                current.id = JSON.parse(responseBody).id;
                current.update();
            } else {
                gs.error(log_name + "User Creation | FAILED. Reason: " + JSON.parse(responseBody).message);
            }

        } else {
            gs.error(log_name + "User Creation | Unable to get the token. Reason: " + JSON.parse(responseBody).message);
        }

    } catch (ex) {
        gs.error(log_name + "User Creation | Error Occured while executing. Error Message - " + ex.message);
    }

})(current, previous);
