sys_script_include.script:
var CommonMethods = Class.create();
CommonMethods.prototype = {
	initialize: function() {
		this.log = "SI - CommonMethods | ";
	},

	type: 'CommonMethods',

	getRecord: function(table, sysID){
		var logName = this.log+"GetRecord >> ";
		var data = {};
		data.isValid = false;


		if(!gs.tableExists(table)){
			gs.error(logName+"Given table: "+table+" doesn't exists.");
			return data;
		}else{
			gs.info(logName + "Table exists");
		}
		var gr = new GlideRecord(table);
		gr.addQuery('sys_id', sysID);
		gr.query();

		if(!gr.hasNext()){
			gs.error(logName+" Given record with sysId: "+sysID+" doesn't exists in the table:"+table);
			return data;
		}else{
			gs.info(logName+"Data exists with sysId. Fetching it...");
		}
		var result = {};
		gr.next();
		for(var key in gr){
			result[key] = gr.getValue(key);
		}
		data.isValid = true;
		data.getData = function(){
			return result;
		};
		return result;
	},

	getUserDataByUID(uID){
		var logName = this.log+"getUserDataByUID >> ";
		if(!this.isAdmin){
			gs.error(log+" Unknown Access found");
			return;
		}
		gr = new GlideRecord('sys_user');
		gr.addEncodedQuery('user_name=' + uID);
		gr.query();
		var result;
		if (gr.hasNext()) {
			gr.next();
			result = {
				firstName: gr.first_name.toString(),
				lastName: gr.last_name.toString(),
				mailID: gr.email.toString(),
				uID: uID,
			};
		}
		return result;
	},

	isAdmin : function(){
		return gs.getUser().isMemberOf(this.adminGroupID);
	},
};
