sys_script_include.script:
var CommonMethods = Class.create();
CommonMethods.prototype = {
    initialize: function() {
    },

    type: 'CommonMethods',

	getRecord: function(table, sysId){
		var logName = "GetRecord >> ";
		if(!gs.tableExists(table)){
			gs.error(logName+"Given table: "+table+" doesn't exists.");
			return;
		}

		var gr = new GlideRecord(table);
		gr.addActiveQuery();
		gr.query();

		if(!gr.hasNext()){
			gs.error(logName+" Given record with sysId: "+sysId+" doesn't exists in the table:"+table);
			return;
		}

		var data = {};
		gr.next();
		for(var key in gr){
			data[key] = gr.getValue(key);
		}

		return data;
	}
};
