sysauto_script.script:
var logName = "ScheduledScript | ";
var apcf = new APCommonFunctions();
var token = apcf.getToken();
var req = new sn_ws.RESTMessageV2('x_snc_accessprov_0.Automation Anyhere - API', 'List Roles');
req.setStringParameterNoEscape("authorization", token);
var response = req.execute();
var responseBody = response.getBody();
var httpStatus = response.getStatusCode();
gs.info(logName + " Response Status: " + httpStatus);

if (httpStatus == 200){
	var rolesList = JSON.parse(responseBody).list;
	var gr = new GlideRecord("x_snc_accessprov_0_aa_roles");
	rolesList.forEach(function(role){
		gr.addQuery('id', role.id);
		gr.query();
		if(!gr.next()){
			gs.info(logName + "Role doesn't exists. Adding it");
			gr.initialize();
			gr.setValue("id", role.id);
			gr.setValue("name", role.name);
			gr.setValue("description", role.description);
			gr.insert();
		}
	});
}else{
	gs.error(logName + " Error Occured. "+responseBody);
}
