sp_widget.client_script:
api.controller=function(spModal, $scope, spUtil) {
	/* widget controller */
	var c = this;

	c.data.user = {
		username: c.data.iUserInfo.username,
		firstName: c.data.iUserInfo.firstName,
		lastName: c.data.iUserInfo.lastName,
		mailID: c.data.iUserInfo.mailID,
		isDevice: c.data.iUserInfo.isDevice,
		userID: c.data.iUserInfo.userID,
		recordSysID: c.data.iUserInfo.recordSysID
	}

	c.data.user.roles = {
		displayValue: c.data.iTmpRoleNames,
		value: c.data.iTmpRoleIDs
	}


	if(c.data.user.isDevice){
		c.data.licenseOptions = c.data.iAvailableLicenses;
		c.data.licenseOptions = c.data.licenseOptions.concat(c.data.iUserInfo.licenses[0]);
	}


	$scope.$on("field.change", function (evt, params) {
		c.data.user.roles = {
			displayValue: params.displayValue,
			value: params.newValue
		};
	});

	console.log(c.data);

	function areSame(a,b){
		return a == b;
	}

	function anyChanges(previous, current){
		if(!areSame(previous.firstName,current.firstName) || 
			 !areSame(previous.lastName,current.lastName) ||
			 !areSame(previous.mailID,current.mailID) ||
			 (current.isDevice && !areSame(previous.licenses[0].value, current.licenseSelected.value)) ||
			 ! areSame(c.data.iTmpRoleNames,current.roles.displayValue) ||
			 ! areSame(c.data.iTmpRoleIDs,current.roles.value)){
			return true;
		}
		return false;
	}

	function handleUpdateResponse(res) {
		c.server.refresh();
	}

	function handleError(error) {
		// Handle or log the error as needed
		console.error('An error occurred:', error);
	}

	c.updateUserData = function () {
		console.log(c.data);
		if(c.data.user.mailID != "" && c.data.user.firstName !="" && c.data.user.lastName !="" && c.data.user.roles.value !=""){
			if(anyChanges(c.data.iUserInfo, c.data.user)){
				c.data.action = "update";
				c.server.update().then(handleUpdateResponse).catch(handleError);
			}else{
				spUtil.addInfoMessage("No Changes Made!");
			}
		}else{
			spUtil.addErrorMessage("Please fill required fields.")
		}


	};
};

