sp_widget.script:
(function () {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.isRecordExists = false;

	var requestSysID = '3975bbbb97014210788572d6f053afb2';//$sp.getParameter('requestID');

	var baseCls = new BaseClass();
	data.iRolesTable = baseCls.getRolesTableName();
	// data.licensesTable = baseCls.getLicenseTableName();

	var cls = new CommonClass();
	data.iUserInfo = cls.getUserByID(requestSysID);
	data.iAvailableLicenses = cls.getAvailableCRLicenses();

	if (data.iUserInfo) {
		data.isRecordExists = true;
	}

	// Roles
	var tmpRoleNames = [];
	var tmpRoleIDs = [];
	data.iUserInfo.roles.forEach(function (role) {
		tmpRoleNames.push(role.name);
		tmpRoleIDs.push(role.id);
	});

	data.iTmpRoleNames = tmpRoleNames.join('####');
	data.iTmpRoleIDs = tmpRoleIDs.join(',');

	// // Licenses
	// var tmpLicenseNames = [];
	// var tmpLicenseIDs = [];
	// data.iUserInfo.roles.forEach(function(license){
	// 	tmpLicenseNames.push(license.name);
	// 	tmpLicenseIDs.push(license.id);
	// });

	// data.iTmpLicenseNames = tmpLicenseNames.join('####');
	// data.iTmpLicenseIDs = tmpLicenseIDs.join(',');



	if (input) {
		// Update action
		if (input.action === "update") {
			if(input.user.mailID != "" && input.user.firstName !="" && input.user.lastName !="" && input.user.roles.value !=""){
				try {
					var output = cls.updateUser(input.user);
					if (output.isUpdated)
						gs.addInfoMessage("User details has updated successfully")
					else
						gs.addErrorMessage('Error while updating user.\n<b>Reason:</b> ' + output.msg);
				} catch (e) {
					gs.error('Error occurred while updating user info:', e);
					gs.addErrorMessage('An error occurred while updating the data.');
				}
			}else{
				gs.addErrorMessage("Please fill the required fields.");
			}

		}
	}

})();
