sp_widget.script:
(function () {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.recordExists = false;

	var requestSysID = 'db6075ce97cdc610788572d6f053afdd';//$sp.getParameter('requestID');
	var cls = new CommonClass();
	data.userInfo = cls.getUserByID(requestSysID);

	if(data.userInfo){
		data.recordExists = true;
	}

	// Roles
	var tmpRoleNames = [];
	var tmpRoleSysIDs = [];
	data.userInfo.roles.forEach(function(role){
		tmpRoleNames.push(role.name);
		tmpRoleSysIDs.push(role.sysID);
	});

	data.tmpRoleNames = tmpRoleNames.join('####');
	data.tmpRoleSysIDs = tmpRoleSysIDs.join(',');

	// Licenses
	var tmpLicenseNames = [];
	var tmpLicenseIDs = [];
	data.userInfo.roles.forEach(function(license){
		tmpLicenseNames.push(license.name);
		tmpLicenseIDs.push(license.id);
	});

	data.tmpLicenseNames = tmpLicenseNames.join('####');
	data.tmpLicenseIDs = tmpLicenseIDs.join(',');


	var baseCls = new BaseClass();
	data.rolesTable = baseCls.getRolesTableName();
	data.licensesTable = baseCls.getLicenseTableName();


	if (input) {
		// APPROVE action
		if (input.action === "approve") {
			if (input.inputData.roleIDs != "" && input.inputData.roleIDs != undefined)
				try {
					var output = cls.approveUser(input.inputData);
					if (output.isCreated)
						gs.addInfoMessage("User has been created")
					else
						gs.addErrorMessage('Error while creating user.\n<b>Reason:</b> ' + output.errorMsg);
				} catch (e) {
					gs.error('Error approving request:', e);
					gs.addErrorMessage('An error occurred while approving the request.');
				}
			else
				gs.addErrorMessage("Please Fill the (*) fields which are mandatory.");
		}

		// REJECT action
		if (input.action === 'reject') {
			try {
				cls.rejectUser(input.inputData);
				gs.addInfoMessage("User has been rejected");
			} catch (e) {
				gs.error('Error rejecting request:', e);
				gs.addErrorMessage('An error occurred while rejecting the request.');
			}
		}
	}

})();
