sp_widget.client_script:
api.controller=function(spModal, $scope) {
	/* widget controller */
	var c = this;

	c.data.roles = {
		displayValue: c.data.iTmpRoleNames,
		value:c.data.iTmpRoleSysIDs
	}
	$scope.$on("field.change", function (evt, params) {
		c.data.roles = {
			displayValue: params.displayValue,
			value: params.newValue
		};
	});

	c.data.licenseOptions = c.data.iAvailableLicenses;
	c.data.licenseOptions = c.data.licenseOptions.concat(c.data.iUserInfo.licenses[0]);
	if(c.data.iUserInfo.isDevice)
		c.data.licenseSelected = c.data.iUserInfo.licenses[0].detail;


	console.log(c.data);

	// delete object property if exists
	function deleteProp(obj, key) {
		if (obj.hasOwnProperty(key))
			delete obj[key];
		return obj;
	}

	// Reset data properties
	function resetData() {
		deleteProp("inputData");
		deleteProp("action");
		deleteProp("rejectReason");
	}

	function handleUpdateResponse(res) {
		resetData();
		c.server.refresh();
	}

	function handleError(error) {
		// Handle or log the error as needed
		console.error('An error occurred:', error);
		resetData();
	}

	c.approveRequest = function () {
		console.log(c);
		// roles = roles==undefined ? "" : roles;
		// licenses = licenses==undefined ? "" : licenses;

		// if (roles != "" && roles != undefined) {
		// 	spModal.confirm("Are you sure to <b>approve</b> this request?").then(function (confirmed) {
		// 		spUtil.addTrivialMessage("Approving the request...");
		// 		c.data.inputData = {"roleIDs": roles, "recordSysID": c.data.recordSysID, licenses};
		// 		c.data.action = "approve";
		// 		c.server.update().then(handleUpdateResponse).catch(handleError);
		// 	});
		// }
		// else
		// 	spUtil.addErrorMessage("Please select the input for the role.");
	};
};

