sp_widget.script:
(function () {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.isRecordExists = false;

	var requestSysID = '7ab0fa9f97054210788572d6f053afb7';//$sp.getParameter('requestID');

	var baseCls = new BaseClass();
	data.iRolesTable = baseCls.getRolesTableName();
	// data.licensesTable = baseCls.getLicenseTableName();

	var cls = new CommonClass();
	data.iUserInfo = cls.getUserByID(requestSysID);
	data.iAvailableLicenses = cls.getAvailableCRLicenses();

	if(data.iUserInfo){
		data.isRecordExists = true;
	}

	// Roles
	var tmpRoleNames = [];
	var tmpRoleSysIDs = [];
	data.iUserInfo.roles.forEach(function(role){
		tmpRoleNames.push(role.name);
		tmpRoleSysIDs.push(role.sysID);
	});

	data.iTmpRoleNames = tmpRoleNames.join('####');
	data.iTmpRoleSysIDs = tmpRoleSysIDs.join(',');

	// // Licenses
	// var tmpLicenseNames = [];
	// var tmpLicenseIDs = [];
	// data.iUserInfo.roles.forEach(function(license){
	// 	tmpLicenseNames.push(license.name);
	// 	tmpLicenseIDs.push(license.id);
	// });

	// data.iTmpLicenseNames = tmpLicenseNames.join('####');
	// data.iTmpLicenseIDs = tmpLicenseIDs.join(',');



	if (input) {
		// Update action
		if (input.action === "approve") {
			if (input.inputData.roleIDs != "" && input.inputData.roleIDs != undefined)
				try {
					var output = cls.approveUser(input.inputData);
					if (output.isCreated)
						gs.addInfoMessage("User has been created")
					else
						gs.addErrorMessage('Error while creating user.\n<b>Reason:</b> ' + output.errorMsg);
				} catch (e) {
					gs.error('Error approving request:', e);
					gs.addErrorMessage('An error occurred while approving the request.');
				}
			else
				gs.addErrorMessage("Please Fill the (*) fields which are mandatory.");
		}
	}

})();
