sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    var ga = new GlideAjax('UserScript');

    if (isLoading || newValue === '') {
        return;
    } else if (!isLoading && newValue != "") {
        if (g_user.hasRole('x_1131046_aa_man_0.admin')) {
			console.log("Getting value of User which matches with Username");
            ga.addParam('sysparm_name', 'getUserDetailsByUserName');
            ga.addParam('username', g_form.getValue('username'));
            ga.getXML(getResponse);
        }
    }

    function getResponse(response) {
        var paramName = response.responseXML.documentElement.getAttribute('sysparm_name');
        var ans = response.responseXML.documentElement.getAttribute('answer');

		console.log(response);
        if (paramName == 'getUserDetailsByUserName') {
			var res = JSON.parse(ans);
            g_form.setValue('mail_id', res.mailID);
            g_form.setValue('name', res.fullName);
            g_form.setValue('first_name', res.firstName);
            g_form.setValue('last_name', res.lastName);
        }
    }
}
