sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var isValidUser = g_user.getUserID().isMemberOf('AAM - Users');
    if (isValidUser == true){
		var userObj = g_user.getUser();
		g_form.setReadOnly("username", true);
		g_form.setReadOnly("name", true); 
		g_form.setReadOnly("mail_id", true);
        g_form.setDisplay('reason_to_reject', 'false');
		g_form.setDisplay('status', 'false');
		g_form.setValue('username',g_user.getUserName());
		g_form.setValue('name',g_user.getDisplayName());
		g_form.setValue('mail_id',userObj.getEmail());
	}

}
