sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    g_form.setReadOnly("name", true);
    g_form.setReadOnly("mail_id", true);
    g_form.setReadOnly("first_name", true);
    g_form.setReadOnly("last_name", true);

    g_form.setDisplay('reason_to_reject', 'false');
    g_form.setDisplay('status', 'false');

    var ga = new GlideAjax('UserScript');

    if (g_user.hasRole('x_1131046_aa_man_0.user') && !g_user.hasRole('x_1131046_aa_man_0.admin')) {
        //Make all the fields except environment as ReadOnly
        g_form.setReadOnly("username", true);

        g_form.setValue('username', g_user.userName);
        g_form.setValue('name', g_user.fullName);
        g_form.setValue('first_name', g_user.firstName);
        g_form.setValue('last_name', g_user.lastName);

        ga.addParam("sysparm_name", "getUserInfo");
        ga.getXML(getResponse);

    }

    function getResponse(response) {
        var paramName = response.responseXML.documentElement.getAttribute('sysparam_name');
        var res = response.responseXML.documentElement.getAttribute('answer');
        if (paramName == 'getUserInfo') {
            g_form.setValue('mail_id', res);
        } else if (paramName == 'getUserDetailsByUserID') {
            var ans = JSON.parse(res);
        }

    }

}
