var UserScript = Class.create();
UserScript.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    type: 'UserScript',
    userRole: 'x_1131046_aa_man_0.user',
    cmnCls: new CommonClass(),
	recordCls: new RecordClass(),
	log: "SI | UserScript | ",

    getUserInfo: function() {
        var logName = this.log + "getUserInfo >> ";
        gs.info(logName + "Verify User having access");
        if (cmnCls.isValidUser()) {
            gs.info(logName + "User: " + gs.getUserName() + " have access");
            gs.info(logName + "Getting user details");

            // var userInfo = {};
            // userInfo.mailID = gs.getUser().getEmail();
            // gs.info(logName + "Returned the user details");
            // return JSON.stringify(userInfo);

            return gs.getUser().getEmail();
        }else{
			gs.info("User: "+gs.getUserName()+" don't have access");
		}
    },

    getUserDetailsByUserName: function() {
        var logName = this.log + "getUserDetailsByUserName >> ";
        gs.info(logName + "Verify User having access");

        var userName = this.getParameter('username');
        gs.info(logName + "Fetching user data of "+userName+" from RecordClass");
		var user = this.recordCls.getUserDataByUserName(userName);
		gs.info(JSON.stringify(user));
        return JSON.stringify(user);
    }
});
