sys_script_include.script:
var UserScript = Class.create();
UserScript.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    type: 'UserScript',
    userRole: 'x_1131046_aa_man_0.user',

    isValidUser: function() {
        return JSON.stringify(gs.getUser().hasRole(this.userRole));
    },

    getUserInfo: function() {
        var logName = "SI | UserScript >> ";
        gs.info(logName + "Verify User having access");
        if (this.isValidUser) {
            var userInfo = {};
            gs.info(logName + "User: " + gs.getUserName() + " have access");
            userInfo.name = gs.getUser().getDisplayName();
            userInfo.mailID = gs.getUser().getEmail();
            userInfo.username = gs.getUserName();
            gs.info(logName + "Getting user details");
            gs.info(JSON.stringify(userInfo));

            return JSON.stringify(userInfo);
        }


    }
});
